require 'test_helper'
class Qatime::RegisterAPITest < ActionDispatch::IntegrationTest
  def app
    Rails.application
  end

  # 游客账户测试
  test "guests api test" do
    # 游客注册
    post "/api/v1/user/guests", params: { password: "123456", password_confirmation: "123456" }
    assert_response :success, "响应错误"
    res = JSON.parse(response.body)
    id = res['data']['user']['id']
    remember_token = res['data']['remember_token']
    user = User.find(id)

    # 游客资金账户
    get "/api/v1/payment/users/#{id}/cash", headers: { 'Remember-Token' => remember_token }
    assert_response :success, "响应错误"
    res = JSON.parse(response.body)
    assert_equal 0.0, res['data']['balance'].to_f

    # 游客充值
    @account = user.reload.cash_account
    receipt_data = ""
    transaction_id = "1000000293966702"
    assert_difference "@account.reload.balance.to_f", 34.3, "充值到账金额不正确" do
      post "/api/v1/payment/recharges/#{transaction_id}/verify_receipt",
           params: { receipt_data: receipt_data },
           headers: { 'Remember-Token' => remember_token }
    end

    # 游客购买直播课

    # 游客绑定账号

    post "/api/v1/captcha", params: { send_to: '17600010398', key: 'register_captcha' }
    assert_response :success, "响应错误"
    post "/api/v1/user/guests/#{id}/bind",
         params: {
           login_mobile: '17600010398',
           captcha_confirmation: '1234',
           password: "123456",
           password_confirmation: "123456",
           accept: 1,
           name: '姓名'
         },
         headers: { 'Remember-Token' => remember_token }
    assert_response :success, "响应错误"
    assert_equal '17600010398', user.reload.login_mobile
  end
end
